import React, { useEffect, useState } from "react";
import { getPetDetails } from "../../api/petfinder";
import Hero from "../../components/hero";
import { useParams, Navigate } from "react-router-dom";

const PetDetailsPage = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const { id } = useParams();

  useEffect(() => {
    async function fetchPetDetails() {
      try {
        setLoading(true);
        const petData = await getPetDetails(id);
        if (!petData) {
          throw new Error("Pet not found");
        }
        setData(petData);
        setError(false);
      } catch (err) {
        setError(true);
      } finally {
        setLoading(false);
      }
    }

    fetchPetDetails();
  }, [id]);

  if (loading) {
    return <h3>Loading...</h3>;
  }

  if (error || !data) {
    return <Navigate to="/pet-details-not-found" replace />;
  }

  const heroImage =
    data?.photos?.[1]?.full || "https://i.imgur.com/aEcJUFK.png";
  const petImage =
    data?.photos?.[0]?.medium || "https://i.imgur.com/aEcJUFK.png";

  return (
    <div>
      <main>
        <Hero
          image={heroImage}
          displayText={`Meet ${data.name || "Unknown"}`}
        />
        <div className="pet-detail">
          <div className="pet-image-container">
            <img
              className="pet-image"
              src={petImage}
              alt={`${data.name || "Pet"} Image`}
            />
          </div>
          <div>
            <h1>{data.name || "Unknown Pet"}</h1>
            <h3>Breed: {data.breeds?.primary || "Unknown Breed"}</h3>
            <p>Color: {data.colors?.primary || "Unknown"}</p>
            <p>Gender: {data.gender || "Unknown"}</p>
            <h3>Description</h3>
            <p>{data.description || "No description available."}</p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default PetDetailsPage;
